description=#{description}, is_active = #{isActive}, title = #{title}, address_line1 = #{addressLine1}, address_line2 = #{addressLine2}, available_quantity = #{availableQuantity}, city = #{city}, cost_per_item = #{costPerItem}, image_uri = #{imageUri}, state = #{state}, zip_code = #{zipCode}, start_date = #{startDate}, end_date = #{endDate}, close_date = #{closeDate},
where id = #{id} and user_profile_id = #{profileId}
